const timm = {
  clone,
  addLast,
  addFirst,
  removeLast,
  removeFirst,
  insert,
  removeAt,
  replaceAt,

  getIn,
  // eslint-disable-next-line object-shorthand
  set: set,  // so that flow doesn't complain
  setIn,
  updateIn,
  merge,
  mergeIn,
  omit,
  addDefaults,
};

export default timm;
